import './App.css';
import morseCodes from './components/morseCodes'
import morseCodesDotsDashes from "./components/morseCodesDotsDashes";
import React, { Component } from 'react';

class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            input: "",
            output: "",
            radioOption: "",
            showHideMorse: true,
            showHideAffine: false,
            showHideVigenere: false,
        };
        this.hideComponent = this.hideComponent.bind(this);
    }

    downloadInputTxtFile = () => {
        const element = document.createElement("a");
        const file = new Blob([document.getElementById('input').value],
            {type: 'text/plain;charset=utf-8'});
        element.href = URL.createObjectURL(file);
        element.download = "inputFile.txt";
        document.body.appendChild(element);
        element.click();
    }

    downloadOutputTxtFile = () => {
        const element = document.createElement("a");
        const file = new Blob([document.getElementById('output').value],
            {type: 'text/plain;charset=utf-8'});
        element.href = URL.createObjectURL(file);
        element.download = "outputFile.txt";
        document.body.appendChild(element);
        element.click();
    }

    hideComponent(name) {
        switch (name) {
            case "showHideMorse":
                this.setState({showHideMorse: this.state.showHideMorse = true});
                this.setState({showHideAffine: this.state.showHideAffine = false})
                this.setState({showHideVigenere: this.state.showHideVigenere = false})
                break;
            case "showHideAffine":
                this.setState({showHideMorse: this.state.showHideMorse = false});
                this.setState({showHideAffine: this.state.showHideAffine = true})
                this.setState({showHideVigenere: this.state.showHideVigenere = false})
                break;
            case "showHideVigenere":
                this.setState({showHideMorse: this.state.showHideMorse = false});
                this.setState({showHideAffine: this.state.showHideAffine = false})
                this.setState({showHideVigenere: this.state.showHideVigenere = true})
                break;
            default:

        }
    }

    setOption = e => {
        this.radioOption = e.target.value;
    }

    convert(input){
        let showHideMorse = this.state.showHideMorse;
        let showHideAffine = this.state.showHideAffine;
        let showHideVigenere = this.state.showHideVigenere;
        if(showHideMorse === true) {
            let output = "";
            let morseArray = input;
            if(this.radioOption === "encrypt") {
                for (let i = 0; i < morseArray.length; i++) {
                    if (morseArray[i].toUpperCase() === " ") {
                        output += "\xa0"
                    } else {
                        output += "\xa0" + morseCodes[morseArray[i]]
                    }
                }
                this.setState({output, input})
            }

            if(this.radioOption === "decrypt") {
                morseArray = input.split(" ")

                for (let i = 0; i < morseArray.length; i++) {
                    if (morseArray[i].toUpperCase() === " ") {
                        output += "\xa0"
                    } else {
                        output += "\xa0" + morseCodesDotsDashes[morseArray[i]]
                    }
                }
                this.setState({output, input})
            }
        }

        if(showHideAffine === true) {
            let chars = input.split("");
            let currChar = chars;
            let output;
            let a = 7;
            let b = 5;
            let invert = 0;
            let encryptedWord = "";
            let currInt = 0;
            let currEnc = "";
            if(this.radioOption === "encrypt") {
                for (let i = 0; i < chars.length; i++) {
                    currInt = parseInt(currChar[i], 36) - 10;
                    currEnc = ((a * currInt + b) % 26);
                    encryptedWord += (String.fromCharCode(97 + currEnc));
                }
                output = encryptedWord;
                this.setState({output, input});
            }
            if(this.radioOption === "decrypt") {
                for (let i = 0; i < chars.length; i++) {
                    for (let j = 0; j < 26; j++){
                        if ((a * j) % 26 === 1)
                            invert = j;
                    }
                    currInt = parseInt(currChar[i], 36) - 10;
                    currEnc = (invert * (currInt - b) ) % 26;
                    encryptedWord += (String.fromCharCode(97 + currEnc));
                }
                output = encryptedWord;
                this.setState({output, input});
            }
        }

        if(showHideVigenere === true){
            let vigenereArray = input.split("");
            let keyword = "tajne";
            let alphabet = "abcdefghijklmnopqrstuvwxyz";
            let output = "";
            let j = 0;
            if(this.radioOption === "encrypt"){
                let cipherText = "";
                for (let i = 0; i < vigenereArray.length; i++) {
                    const inputCharacter = vigenereArray[i];
                    const inputCharacterIndex = alphabet.indexOf(inputCharacter);
                    if (inputCharacterIndex === -1) {
                        cipherText += inputCharacter;
                        continue;
                    }
                    const keywordCharacter = keyword[j];
                    const keywordCharacterIndex = alphabet.indexOf(keywordCharacter);
                    const indicesSum = inputCharacterIndex + keywordCharacterIndex;
                    const cipherCharacterIndex = indicesSum % 26;
                    const cipherTextCharacter = alphabet[cipherCharacterIndex];
                    j = j === keyword.length - 1 ? 0 : j + 1;
                    cipherText += cipherTextCharacter;
                    output = cipherText;
                }
                this.setState({output, input});
            }

            if(this.radioOption === "decrypt"){
                let cipherText = input.split("");
                let plainText = "";
                for (let i = 0; i < cipherText.length; i++) {
                    const ciphertextCharacter = cipherText[i];
                    const ciphertextCharacterIndex  = alphabet.indexOf(ciphertextCharacter);
                    if (ciphertextCharacterIndex === -1) {
                        plainText += ciphertextCharacter;
                        continue;
                    }
                    const keyCharacter = keyword[j];
                    const keyCharacterIndex = alphabet.indexOf(keyCharacter);
                    const indicesSum = ciphertextCharacterIndex - keyCharacterIndex + 26;
                    const cipherCharacterIndex = indicesSum % 26;
                    const cipherTextCharacter = alphabet[cipherCharacterIndex];
                    j = j === keyword.length - 1 ? 0 : j + 1;
                    plainText += cipherTextCharacter;
                    output = plainText;
                }
                this.setState({output, input});
            }
        }
    }

    render(){
        const {showHideMorse, showHideAffine, showHideVigenere} = this.state;
        return(
            <div className="container">
                <div className="btn-group btn-group-toggle" data-toggle="buttons">
                    <button onClick={() => this.hideComponent("showHideMorse")} className="btn btn-primary"
                            className="btn btn-primary btn-lg">ALFABET MORSE'A
                    </button>
                    <button onClick={() => this.hideComponent("showHideAffine")} className="btn btn-primary"
                            className="btn btn-primary btn-lg">SZYFR AFINICZNY
                    </button>
                    <button onClick={() => this.hideComponent("showHideVigenere")} className="btn btn-primary"
                            className="btn btn-primary btn-lg">SZYFR VIGENERE'A
                    </button>
                </div>

                {showHideMorse && (<div className="row">
                    <div className="col-sm-6">
                        <h1>Alfabet Morse'a</h1>
                    </div>
                </div>)}

                {showHideAffine && (<div className="row">
                    <div className="col-sm-6">
                        <h1>Szyfr Afiniczny</h1>
                        <h3><input className="form-control"
                                   onChange={(e)=>this.convert(e.target.value)}/> </h3>
                        <h3><input className="form-control&"
                                   onChange={(e)=>this.convert(e.target.value)}/> </h3>
                    </div>
                </div>)}

                {showHideVigenere && (<div className="row">
                    <div className="col-sm-6">
                        <h1>Szyfr Vigenere'a</h1>
                    </div>
                </div>)}
                <div className="col-sm-9">
                    <h3><input className="form-control" id="input"
                           onChange={(e)=>this.convert(e.target.value)}/> </h3>
                    <button onClick={this.downloadInputTxtFile} className="btn btn-secondary">Download input</button>
                    <h3><input className="form-control" id="output" value={this.state.output} /> </h3>
                    <button onClick={this.downloadOutputTxtFile} className="btn btn-secondary">Download output</button>
                    <div onChange={event => this.setOption(event)}>
                        <input type="radio" value="encrypt"
                               name="option" id="encrypt"
                               onChange={this.handleOptionChange} />
                               encrypt

                        <input type="radio" value="decrypt"
                               name="option" id="decrypt"
                               onChange={this.handleOptionChange} />
                               decrypt
                    </div>
                </div>
            </div>

        )
    }
}

export default App;
