import logo from './logo.svg';
import './App.css';
import morseCodes from './components/morseCodes'
import React, { Component, useState, useEffect } from 'react';

class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            input: "",
            output: ""
        }
    }

    convertToMorse(input){
        let output = ""
        let morseArray = input.split("")
        // if(morseArray[0] == "." || morseArray[0] == "-") {
        //     morseArray = input.split(" ")
        // }
        // else {
        //     morseArray = input.split("")
        // }
        for(let i = 0; i < morseArray.length; i++){
            if(morseArray[i] == "-" || "."){
                morseArray = input.split(" ")
            }

            if(morseArray[i].toUpperCase() == " "){
                output += "\xa0"
            }

            else {
                output += "\xa0" + morseCodes[morseArray[i].toUpperCase()]
            }
        }
        this.setState({output, input})
    }

    render(){
        return(
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <h1>Morse Code Translator</h1>
                        <input className="form-control"
                               onChange={(e)=>this.convertToMorse(e.target.value)}/>
                        <h3>original: <input className="form-control" value={this.state.input} /> </h3>
                        <h3>morse: <input className="form-control" value={this.state.output} /> </h3>
                    </div>
                </div>
            </div>
        )
    }
}


export default App;
