import './App.css';
import morseCodes from './components/morseCodes'
import React, { Component } from 'react';

class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            input: "",
            output: ""
        }
    }

    convertToMorse(input){
        let output = ""
        let morseArray = input
        if(morseArray[0] == "." || morseArray[0] == "-") {
            morseArray = input.split(" ")
        }
        else {
            morseArray = input.split("")
        }
        for(let i = 0; i < morseArray.length; i++){
            if(morseArray[i].toUpperCase() == " "){
                output += "\xa0"
            }

            else {
                output += "\xa0" + morseCodes[morseArray[i].toUpperCase()]
            }
        }
        this.setState({output, input})
    }

    render(){
        return(
            <div className="container">
                <button>ALFABET MORSE'A</button>
                <button>SZYFR AFINICZNY</button>
                <button>SZYFR VIGENERE'A</button>
                <div className="row">
                    <div className="col-sm-6">
                        <h1>Translator Alfabetu Morse'a</h1>
                        <input className="form-control"
                               onChange={(e)=>this.convertToMorse(e.target.value)}/>
                        <h3><input className="form-control" value={this.state.output} /> </h3>
                    </div>
                </div>

                <div className="row">
                    <div className="col-sm-6">
                        <h1>Translator Alfabetu Morse'a</h1>
                        <input className="form-control"
                               onChange={(e)=>this.convertToMorse(e.target.value)}/>
                        <h3><input className="form-control" value={this.state.output} /> </h3>
                    </div>
                </div>
            </div>

        )
    }
}


export default App;
