import './App.css';
import morseCodes from './components/morseCodes'
import convertToMorse from './components/morseConverter'
import React, { Component } from 'react';

class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            input: "",
            output: ""
        }
    }

    render(){
        return(
            <div className="container">
                <button>ALFABET MORSE'A</button>
                <button>SZYFR AFINICZNY</button>
                <button>SZYFR VIGENERE'A</button>
                <div className="row">
                    <div className="col-sm-6">
                        <h1>Translator Alfabetu Morse'a</h1>
                        <input className="form-control"
                               onChange={(e)=>this.convertToMorse(e.target.value)}/>
                        <h3><input className="form-control" value={this.state.output} /> </h3>
                    </div>
                </div>

                <div className="row">
                    <div className="col-sm-6">
                        <h1>Translator Alfabetu Morse'a</h1>
                        <input className="form-control"
                               onChange={(e)=>this.convertToMorse(e.target.value)}/>
                        <h3><input className="form-control" value={this.state.output} /> </h3>
                    </div>
                </div>
            </div>

        )
    }
}


export default App;
