import logo from './logo.svg';
import './App.css';
import morseCodes from './components/morseCodes'
import React, { useState, useEffect } from 'react';

const App = () => {


    function convertToMorse(input) {
        let output = ""
        let morseArray = input.split("")
        for (let i = 0; i < morseArray.length; i++) {
            if (morseArray[i].toUpperCase() == " ") {
                output += "\xa0\xa0"
            } else {
                output += morseCodes[morseArray[i].toUpperCase()]
            }
        }
        this.setState({output, input})
    }

    const [firstVal, setFirstVal] = useState();
    const [secondVal, setSecondVal] = useState();

    function changeFirstValue(e) {
        setSecondVal(e.target.value);
    }

    function changeSecondValue(e) {
        setFirstVal(e.target.value);
        setSecondVal(convertToMorse(setFirstVal(e.target.value)));
    }

        return (
            <div>
                <input
                    type='text'
                    value={firstVal}
                    onChange={changeSecondValue}
                ></input>

                <input
                    type='text'
                    value={secondVal}
                    onChange={changeFirstValue}
                ></input>
            </div>
        );
}

export default App;
