import './App.css';
import morseCodes from './components/morseCodes'
import React, { Component } from 'react';

class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            input: "",
            output: "",
            showHideMorse: true,
            showHideAffine: true,
            showHideVigenere: true
        };
        this.hideComponent = this.hideComponent.bind(this);
    }

    hideComponent(name) {
        switch (name) {
            case "showHideMorse":
                this.setState({showHideMorse: !this.state.showHideMorse});
                break;
            case "showHideAffine":
                this.setState({showHideAffine: !this.state.showHideAffine});
                break;
            case "showHideVigenere":
                this.setState({showHideVigenere: !this.state.showHideVigenere});
                break;
            default:

        }
    }

    convertToMorse(input){
        let output = ""
        let morseArray = input
        if(morseArray[0] == "." || morseArray[0] == "-") {
            morseArray = input.split(" ")
        }
        else {
            morseArray = input.split("")
        }
        for(let i = 0; i < morseArray.length; i++){
            if(morseArray[i].toUpperCase() == " "){
                output += "\xa0"
            }

            else {
                output += "\xa0" + morseCodes[morseArray[i].toUpperCase()]
            }
        }
        this.setState({output, input})
    }

    render(){
        const {showHideMorse, showHideAffine, showHideVigenere} = this.state;
        return(
            <div className="container">
                <button onClick={() => this.hideComponent("showHideMorse")}>ALFABET MORSE'A</button>
                <button>SZYFR AFINICZNY</button>
                <button>SZYFR VIGENERE'A</button>
                {showHideMorse && (<div className="row" id="morse">
                    <div className="col-sm-6">
                        <h1>Translator Alfabetu Morse'a</h1>
                        <input className="form-control"
                               onChange={(e)=>this.convertToMorse(e.target.value)}/>
                        <h3><input className="form-control" value={this.state.output} /> </h3>
                    </div>
                </div>)}

                {showHideAffine && (<div className="row" id="affine">
                    <div className="col-sm-6">
                        <h1>Translator Alfabetu Morse'a</h1>
                        <input className="form-control"
                               onChange={(e)=>this.convertToMorse(e.target.value)}/>
                        <h3><input className="form-control" value={this.state.output} /> </h3>
                    </div>
                </div>)}
            </div>

        )
    }
}


export default App;
